import React, { useState, useEffect } from 'react';
import Papa from 'papaparse';

function CsvLoader({ onDataLoaded }) {
    const [data, setData] = useState([]);

    useEffect(() => {
        // Load the CSV file and parse it
        Papa.parse('/crops.csv', {
            download: true,
            header: true, // If your CSV has a header row
            complete: (results) => {
                setData(results.data);
                onDataLoaded(results.data); // Pass data back to the parent component
            },
            error: (error) => {
                console.error('Error parsing CSV:', error);
            },
        });
    }, []);

    return null; // This component only handles loading CSV, no need to render anything
}

export default CsvLoader;
