import React, { useState } from 'react';
import CsvLoader from './components/CsvLoader';
import './App.css';

function App() {
  const [counties, setCounties] = useState([]); // Store counties data from CSV
  const [selectedCounty, setSelectedCounty] = useState(''); // Store selected county
  const [selectedFruit, setSelectedFruit] = useState(''); // Store selected fruit
  const [availableFruits, setAvailableFruits] = useState([]); // Store available fruits based on district

  // Hardcoded fruits by district for now
  const fruitsByDistrict = {
    'Blacklands': ['Tomatoes', 'Peppers', 'Squash'],
    'Southern High Plains': ['Melons', 'Peppers'],
    // Add more districts and fruits as needed
  };

  const handleDataLoaded = (csvData) => {
    console.log('Loaded CSV Data:', csvData); // Log the parsed CSV data
    setCounties(csvData); // Store the parsed CSV data
  };

  const handleCountyChange = (e) => {
    const county = e.target.value;
    setSelectedCounty(county);

    // Find the district based on the selected county
    const countyData = counties.find(c => c['County Name'] === county); // Adjust "County Name" based on your CSV header
    if (countyData && fruitsByDistrict[countyData.District]) {
      setAvailableFruits(fruitsByDistrict[countyData.District]); // Set fruits based on district
    } else {
      setAvailableFruits([]); // Clear fruits if no district match
    }
  };

  const handleFruitChange = (e) => {
    setSelectedFruit(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(`Selected County: ${selectedCounty}, Selected Fruit: ${selectedFruit}`);
  };

  return (
    <div className="App">
      <header>
        <h1>Texas Crop Rotation Planner</h1>
        {/* Mission Statement */}
        <section className="mission">
          <h2>Our Mission</h2>
          <p>
            Our mission is to provide Texas farmers with sustainable crop rotation solutions that promote soil health, optimize crop yield, and reduce environmental impact. We believe in empowering farmers with data-driven tools to make informed decisions about the best crops for their region.
          </p>
        </section>
      </header>

      <h1>Crop Rotation Planner</h1>

      {/* Load and parse the CSV data */}
      <CsvLoader onDataLoaded={handleDataLoaded} />

      {/* Dropdown for county selection */}
      <form onSubmit={handleSubmit} className="form-group">
        <label htmlFor="county-select" className="form-label">Select County:</label>
        <select id="county-select" value={selectedCounty} onChange={handleCountyChange} className="form-select">
          <option value="">--Choose a county--</option>
          {Array.from(new Set(crops.map(crop => crop.District))).map((district, index) => (
            <option key={index} value={district}>
              {district}
            </option>
          ))}
        </select>

        <label htmlFor="crop-type-select" className="form-label">Select Crop Type:</label>
        <select id="crop-type-select" value={selectedCropType} onChange={handleCropTypeChange} className="form-select">
          <option value="">--Choose a crop type--</option>
          <option value="Legume">Legume</option>
          <option value="Root">Root</option>
          <option value="Fruit">Fruit</option>
          <option value="Leaf">Leaf</option>
        </select>

        <button type="submit" className="btn btn-primary">Get Crop Recommendations</button>
      </form>
    </div>
  );
}

export default App;
